var MetaWeather = require('./index');
var console = window.console;

var mw = new MetaWeather();

mw.search().query('san fran').then(function(response) {
	console.log('Search for "san fran": ' + response.statusCode);
});

mw.search().latLon('something').then(function(response) {

}).catch(function(error) {
	console.log('Should return error: ' + error.statusCode);
});

mw.search().latLon('0, -122.41964').then(function(response) {
	console.log('Search for "0, -122.41964" with latLon: ' + response.statusCode);
});

mw.search().latLon({lat: 37.777119, lon: -122.41964}).then(function(response) {
	console.log('Search for "0, -122.41964" with latLon (object): ' + response.statusCode);
});

mw.location(2487956).then(function(response) {
	console.log('Search location with woeid: ' + response.statusCode);
});

mw.location(2487956, '2017-04-05').then(function(response) {
	console.log('Search location with woeid and date: ' + response.statusCode);
});

mw.location({woeid: 2487956, date: '2017-04-05'}).then(function(response) {
	console.log('Search location with woeid and date (object): ' + response.statusCode);
});

mw.location({woeid: 2487956, date: '2017/04/05'}).then(function(response) {
	console.log('Search location with woeid and date (object): ' + response.statusCode);
});
